import React from 'react';
import ButtonIcon from '../ButtonIcon';
import { allowedThemes as buttonThemes, allowedSizes as buttonSizes } from '../../button/Button';

export default {
    title: 'Atoms/Button Icon',
    component: ButtonIcon
};

export const Default = {
    render: () => (
        <ButtonIcon theme="primary" icon="plus">
            Add
        </ButtonIcon>
    ),
    name: 'Default'
};

export const IconRight = {
    render: () => (
        <ButtonIcon theme="primary" icon="plus" iconPosition="right">
            Add
        </ButtonIcon>
    ),
    name: 'Icon right'
};

export const Disabled = {
    render: () => (
        <ButtonIcon theme="primary" icon="plus" disabled>
            Add
        </ButtonIcon>
    ),
    name: 'Disabled'
};

export const Selected = {
    render: () => (
        <ButtonIcon theme="primary" icon="plus" selected>
            Add
        </ButtonIcon>
    ),
    name: 'Selected'
};

export const OnlyText = {
    render: () => <ButtonIcon theme="primary">Add</ButtonIcon>,
    name: 'Only text'
};

export const OnlyIcon = {
    render: () => <ButtonIcon theme="primary" icon="plus" />,
    name: 'Only icon'
};

export const NotClickable = {
    render: () => (
        <ButtonIcon theme="emphasisAlt-outline" icon="check" notClickable>
            Added to Cart
        </ButtonIcon>
    ),
    name: 'Not clickable'
};

export const Sizes = {
    render: () => buttonSizes.map(size => (
        <div
            key={size}
            style={{
                display: 'flex',
                justifyContent: 'space-between',
                padding: '5px 10px'
            }}
        >
            <ButtonIcon theme="primary" size={size}>
                {size}
            </ButtonIcon>
            <ButtonIcon theme="primary" size={size} icon="plus">
                {size}
            </ButtonIcon>
        </div>
    )),

    name: 'Sizes'
};

export const CustomIcon = {
    render: () => (
        <ButtonIcon theme="primary" iconSrc="https://via.placeholder.com/20">
            Custom Icon
        </ButtonIcon>
    ),
    name: 'Custom icon'
};

export const LongText = {
    render: () => (
        <div
            style={{
                width: 300
            }}
        >
            <ButtonIcon theme="primary" icon="plus">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec at dui id nunc iaculis vulputate sed ut
                nibh.
            </ButtonIcon>
        </div>
    ),

    name: 'Long text'
};

export const ThemeButtons = {
    render: () => buttonThemes.map(theme => (
        <div
            key={theme}
            style={{
                display: 'flex',
                justifyContent: 'space-between',
                padding: '5px 10px'
            }}
        >
            <ButtonIcon theme={theme}>{theme}</ButtonIcon>
            <ButtonIcon theme={theme} icon="plus">
                {theme}
            </ButtonIcon>
        </div>
    )),

    name: 'Theme buttons'
};

export const ThemeLinks = {
    render: () => buttonThemes
        .filter(theme => theme !== 'card' || theme !== 'emphasis-outline')
        .map(theme => (
            <div
                key={theme}
                style={{
                    width: 300,
                    padding: '5px 10px',
                    background: theme === 'white' || theme === 'secondary' ? '#000' : 'transparent'
                }}
            >
                <ButtonIcon theme={theme} type="link" icon="plus">
                    {theme}
                </ButtonIcon>
            </div>
        )),

    name: 'Theme links'
};
