import React from 'react';
import FormLabel from '../FormLabel';

export default {
    title: 'Atoms/Form Label',
    component: FormLabel
};

export const Default = {
    render: () => <FormLabel labelText="Label" htmlFor="name" />,
    name: 'Default'
};

export const Required = {
    render: () => <FormLabel labelText="Label" required />,
    name: 'Required'
};

export const NoLabelAndNoChildren = {
    render: () => <FormLabel />,
    name: 'No label and no children'
};

export const HiddenLabel = {
    render: () => (
        <FormLabel labelText="Label" htmlFor="name" hiddenText>
            <input name="name" />
        </FormLabel>
    ),

    name: 'Hidden label'
};

export const WithInput = {
    render: () => (
        <FormLabel labelText="Label" required htmlFor="name">
            <input name="name" required />
        </FormLabel>
    ),

    name: 'With input'
};
