const controlsToInclude = Object.values(getArgTypes(HeaderCategoriesMenu.schema().form)).map(arg => arg.name);

export default {
    title: 'Global Components/Header/Atoms/Header Categories Menu',
    component: HeaderCategoriesMenu,
    argTypes: getArgTypes(HeaderCategoriesMenu.schema().form),
    parameters: {
        controls: {
            include: controlsToInclude
        },
        layout: 'fullscreen'
    }
};

export const Default = {
    render: args => (
        <HeaderCategoriesMenu
            categories={headerCategoriesMock}
            settings={args}
        />
    ),
    args: getArgs(HeaderCategoriesMenu.schema().form)
};
