import React from 'react';
import PropTypes from 'prop-types';
import withListener from '../withListener';
import { ChildrenOrderableContainer } from './ChildrenOrderableContainer';
import { orderableComponentsShape } from '../../constants/components/prop-types/orderable-component';
import { getOrderableComponents } from '../../tools/pageOrderableHelper';

export const HeaderChildrenOrderableContainer = ({ orderableComponents }) => {
    const { HeaderOrderableComponents } = getOrderableComponents();

    return (
        <ChildrenOrderableContainer
            orderableComponents={orderableComponents}
            componentsList={HeaderOrderableComponents}
        />
    );
};

HeaderChildrenOrderableContainer.schema = () => ({
    name: 'PageOrderableContainer'
});

HeaderChildrenOrderableContainer.propTypes = {
    orderableComponents: PropTypes.arrayOf(orderableComponentsShape)
};

HeaderChildrenOrderableContainer.defaultProps = {
    orderableComponents: []
};

export default withListener(HeaderChildrenOrderableContainer);
