import { gql } from '@apollo/client';

export const PRODUCT_VARIANTS_GRAPHQL_QUERY = gql`
    query fetchMarketplaceProduct($productId: ID!) {
        marketplaceProduct(id: $productId) {
            id
            name
            variantOptions {
                code
                name
                values {
                    code
                    name
                    hexcode
                    secondaryInformation
                }
                defaultValue {
                    code
                    name
                    hexcode
                }
                inputType
            }
            variants {
                id
                editionId
                isEnabled
                configuration {
                    optionCode
                    valueCode
                }
            }
            editions {
                id
                code
            }
            supplierId
        }
    }
`;
