// should reflect the css styles/definitions/_breakpoints.scss

export const bXXSmall = 330;
export const bXSmall = 480;
export const bSmall = 576;
export const bMedium = 768;
export const bLarge = 992;
export const bXLarge = 1200;
export const bXXLarge = 1330;

export const breakpoints = {
    xxSmall: bXXSmall,
    xSmall: bXSmall,
    small: bSmall,
    medium: bMedium,
    large: bLarge,
    xLarge: bXLarge,
    xxLarge: bXXLarge
};

export const isBreakpointDown = treshold => window.matchMedia(`(max-width: ${treshold - 0.02}px)`).matches;
export const isBreakpointUp = treshold => window.matchMedia(`(min-width: ${treshold}px)`).matches;
