import { darken, lighten, isDark } from '../tools/colorHelper';

export const themeBaseColors = ['primaryColor', 'secondaryColor', 'emphasisColor', 'altEmphasisColor'];
export const colorTransforms = [
    {
        name: 'Active',
        rule: color => darken(color, 0.15)
    },
    {
        name: 'Hover',
        rule: color => lighten(color, 0.15)
    },
    {
        name: 'Text',
        rule: color => (isDark(color) ? '#fff' : '#474747')
    }
];
