import PropTypes from 'prop-types';
import { callToActionsPropTypes } from './call-to-actions';
import { customersPropTypes } from './customers';
import { benefitsPropTypes } from './benefits';
import { i18nPropTypes } from './i18n';

export const overviewPropTypes = {
    title: PropTypes.string,
    description: PropTypes.string,
    callToActions: PropTypes.shape(callToActionsPropTypes),
    image: PropTypes.string,
    imageSrcSet: PropTypes.shape({
        default: PropTypes.string
    }),
    showThirdPartyLegalDisclosure: PropTypes.bool,
    documentationUrl: PropTypes.string,
    downloadDocumentationUrl: PropTypes.string,
    pricingUrl: PropTypes.string,
    customers: PropTypes.arrayOf(PropTypes.shape(customersPropTypes)),
    benefits: PropTypes.arrayOf(PropTypes.shape(benefitsPropTypes)),
    i18n: PropTypes.shape(i18nPropTypes)
};
