import PropTypes from 'prop-types';

export const paginationPropTypes = {
    currentPage: PropTypes.number,
    itemsPerPage: PropTypes.number,
    nbPages: PropTypes.number,
    pagesList: PropTypes.arrayOf(PropTypes.shape({
        nb: PropTypes.number,
        status: PropTypes.oneOf(['disabled', 'selected', 'enabled']),
        type: PropTypes.oneOf(['previous', 'page', 'next', 'ellipsis']),
        url: PropTypes.string
    })),
    simplePagesList: PropTypes.string,
    start: PropTypes.number,
    totalCount: PropTypes.number
};
