export const URL_VALIDATION = /^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?[a-z0-9]+([-.]{1}[a-z0-9]+)*\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/g;

/**
 * regex blocks strings that contains any special character
 *
 * !"#$%&'()*+,-./     \x21-\x2f
 * :;<=>?@             \x3a-\x40
 * [\]^_`              \x5b-\x60
 * {|}~                \x7b-\x7f
 */
export const SPECIAL_CHARACTERS_BLOCKLIST = /^[^!"#$%&'()*+,-./:;<=>?@[\\\]^_`{|}~]*$/gm;
