import { getVideoThumbnail } from '../videos/videoHelpers';

export const hasScreenshots = screenshots => Boolean(screenshots && screenshots.items && screenshots.items.length);
export const hasDemoVideo = overview => Boolean(overview && overview.callToActions && overview.callToActions.demo);

export const getSlides = screenshots => [
    ...(hasScreenshots(screenshots) ? [...screenshots.items] : [])
];

export const getVideoSlide = async demo => {
    const { label: name, iFrameFriendlyUrl: videoUrl } = demo;
    const videoThumbnail = await getVideoThumbnail(videoUrl);

    return ({
        name,
        videoUrl,
        imageUrl: videoThumbnail
    });
};
