import { replacePlaceholders } from '../stringTools';

export const getDomainActionLabel = (domainName, i18nDomains = {}) => {
    const { removeDomain, selectedDomain, selectDomain } = i18nDomains;

    return ({
        removeLabel: replacePlaceholders(removeDomain, { domainName }),
        selectedLabel: replacePlaceholders(selectedDomain, { domainName }),
        selectLabel: replacePlaceholders(selectDomain, { domainName })
    });
};
