export const getNumberOfItemsThatFit = (menuItemsRefs, moreButtonRef, containerWidth = 0) => {
    if (!menuItemsRefs
        || !menuItemsRefs.current
        || !menuItemsRefs.current.length
        || !moreButtonRef
        || !moreButtonRef.current) {
        return null;
    }

    const menuItemsWidth = menuItemsRefs.current.map(elem => elem.getBoundingClientRect().width);
    const btnMoreWidth = moreButtonRef.current.getBoundingClientRect().width;

    let numberOfItemsThatFit = menuItemsRefs.current.length;
    let sumOfMenuItemsWidth = menuItemsWidth.reduce((a, b) => a + b);

    // Find number of items that fit
    while ((sumOfMenuItemsWidth + btnMoreWidth) > containerWidth && numberOfItemsThatFit > 0) {
        sumOfMenuItemsWidth -= menuItemsWidth[numberOfItemsThatFit - 1];
        numberOfItemsThatFit -= 1;
    }

    return numberOfItemsThatFit;
};
