export const getButtons = (links, settings = {}) => links && Object.keys(links).reduce((accumulator, key) => {
    const button = links[key];

    if (typeof button.isVisible === 'undefined' || button.isVisible) {
        accumulator.push(Object.assign(button, {
            key,
            title: settings[key] || button.title,
            isLink: key === 'assignProducts',
            icon: settings[`${key}Icon`]
        }));
    }

    return accumulator;
}, []);
