export const getA11yPricingText = props => {
    const {
        price,
        formattedPrice,
        caption,
        symbol,
        unit,
        frequency,
        textSeparator
    } = props;

    let priceTextForSRs = '';

    if (caption) {
        priceTextForSRs += `${caption} `;
    }

    if (formattedPrice) {
        priceTextForSRs += formattedPrice;
    } else {
        priceTextForSRs += `${symbol || ''}${price}`;
    }

    if (unit) {
        priceTextForSRs += ` ${unit}`;
    }

    if (frequency) {
        priceTextForSRs += `${textSeparator && ` ${textSeparator}`} ${frequency}`;
    }

    return priceTextForSRs;
};

export const getFormattedFullPriceElements = (unitPrice = [], flatPrice) => {
    if (flatPrice) {
        return flatPrice.formattedFullPriceElements;
    }
    const [{ usageRange = [{}] }] = unitPrice;
    const [{ formattedFullPriceElements }] = usageRange;
    return formattedFullPriceElements;
};
