export const getPropValue = (object, targetKey, fallback) => {
    const keys = targetKey.split('.');
    const propValue = keys.reduce((targetValue, key) => (
        targetValue === undefined || targetValue === null ? targetValue : targetValue[key]
    ), object);
    if (!propValue) {
        if (fallback || fallback === 0 || fallback === '') {
            return fallback;
        }
        return propValue;
    }
    return propValue;
};
