import { responsiveStore } from '../../store/responsive';

/*
    In a helper to make sure that the responsive store is always
    initialized (computeResponsive) without having to put it in each component that uses it
*/
export const useResponsive = () => {
    const { breakpoints, computeResponsive } = responsiveStore();
    computeResponsive();
    return breakpoints || {};
};
