import React from 'react';
import Alert, { alertTypes } from '../Alert';

export default {
    title: 'Atoms/Alert',
    component: Alert,
    render: args => <Alert {...args} />,
    argTypes: {
        alertMessage: {
            control: 'text',
            description: 'The message to display in the alert'
        }
    },
    parameters: {
        controls: {
            include: ['alertMessage']
        }
    }
};

export const Success = {
    args: {
        alertMessage: 'Yay success',
        type: alertTypes.SUCCESS_ALERT
    }
};

export const Error = {
    args: {
        alertMessage: 'Oh no, error',
        type: alertTypes.ERROR_ALERT
    }
};
