import React from 'react';
import CarouselSlideImage from '../CarouselSlideImage';
import { getArgs, getArgTypes } from '../../../tools/controlsHelper';
import { imageSlideMockData } from '../../../../mocks/home/carousel';

const getWrappedArgs = args => ({
    ...args,
    ...imageSlideMockData.slideData,
    imageUrl: imageSlideMockData.slideData.image.url
});

export default {
    title: 'Atoms/Carousel Slide/Image',
    component: CarouselSlideImage,
    render: args => (
        <div style={{ height: '440px' }}>
            <CarouselSlideImage data={args} />
        </div>
    ),
    argTypes: getArgTypes(CarouselSlideImage.schema().form),
    parameters: {
        controls: {
            include: Object.keys(CarouselSlideImage.schema().form),
            sort: 'alpha'
        }
    }
};

export const WithControls = {
    args: getWrappedArgs(getArgs(CarouselSlideImage.schema().form))
};
