import React from 'react';
import CarouselSlideText from '../CarouselSlideText';
import { getArgTypes, getArgs } from '../../../tools/controlsHelper';
import { textSlideMockData } from '../../../../mocks/home/carousel';

const getWrappedArgs = args => ({
    ...args,
    ...textSlideMockData.slideData,
    imageUrl: textSlideMockData.slideData.image.url
});

export default {
    title: 'Atoms/Carousel Slide/Text',
    component: CarouselSlideText,
    render: args => (<div style={{ height: '440px' }}><CarouselSlideText data={args} /></div>),
    argTypes: getArgTypes(CarouselSlideText.schema().form),
    parameters: {
        controls: {
            include: Object.keys(CarouselSlideText.schema().form)
        }
    }
};

export const Default = {
    args: getWrappedArgs(getArgs(CarouselSlideText.schema().form))
};
