import React from 'react';
import CartAddonPreview from '../CartAddonPreview';
import { cartMock } from '../../../../mocks/cart';

export default {
    title: 'Atoms/Cart Addon Preview',
    component: CartAddonPreview,
    argTypes: {
        name: {
            control: 'text',
            description: 'The name of the addon'
        }
    },
    render: ({ name }) => {
        const { branding, ...rest } = cartMock.items[0].childItems[0].itemDetails;
        return (
            <CartAddonPreview
                {...rest}
                branding={{ ...branding, name }}
            />
        );
    },
    parameters: {
        controls: {
            include: ['name']
        }
    }
};

export const Default = {
    args: {
        name: 'Addon # 1'
    }
};
