import React, { useState } from 'react';
import DotsNav from '../DotsNav';

const WrappedDotsNav = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    return (
        <div style={{ background: '#eeeeee', padding: '12px' }}>
            <DotsNav nItems={12} currentIndex={currentSlide} goTo={setCurrentSlide} />
        </div>
    );
};

export default {
    title: 'Atoms/Dots Nav',
    component: DotsNav
};

export const Default = {
    render: () => <WrappedDotsNav />,
    name: 'Default'
};
