import React from 'react';
import { createNamespace } from '../../tools/namingTools';
import ButtonIcon from '../button-icon/ButtonIcon';
// IMPORTANT: (s)css should be imported after other atoms/components
import './styles/EditionButtonIcon.scss';

const n = createNamespace('EditionButtonIcon');

const EditionButtonIcon = props => {
    const modifiers = [
        'wide',
        'editionButtonItem'
    ];

    return (
        <ButtonIcon {...props} {...n('button', modifiers).props} />
    );
};

EditionButtonIcon.propTypes = ButtonIcon.propTypes;
EditionButtonIcon.defaultProps = ButtonIcon.defaultProps;

export default EditionButtonIcon;
