import React from 'react';
import { createNamespace } from '../../../tools/namingTools';
import Icon from '../Icon';
import types from '../types';
import '../styles/Icon.story.scss';

const storyRowStyle = {
    display: 'flex',
    alignItems: 'center',
    padding: '4px 12px',
    marginBottom: '6px'
};

const iconWrapperStyle = {
    display: 'flex'
};

const renderIcons = ns => Object.keys(types).map(type => (
    <div style={storyRowStyle} key={`icon-${type}`}>
        <span style={{ marginRight: '20px' }}>{`type ${type}`}</span>
        <div style={iconWrapperStyle}>
            <Icon type={type} {...ns('story', type).props} />
        </div>
    </div>
));

export default {
    title: 'Atoms/Icon',
    component: Icon
};

export const AllTypesAtMatchingSizes = {
    render: () => renderIcons(createNamespace('Icon')),
    name: 'All types at matching sizes'
};

export const WithOtherColorsAndSizes = {
    render: () => renderIcons(createNamespace('IconColored')),
    name: 'With other colors and sizes'
};

export const Ratings = {
    render: () => (
        <div>
            <div style={storyRowStyle}>
                <span
                    style={{
                        marginRight: '20px'
                    }}
                >
                    ratings
                </span>
                <div style={iconWrapperStyle}>
                    <Icon type="star" className="iconRatings_positiveStar" />
                    <Icon type="star" className="iconRatings_positiveStar" />
                    <Icon type="star" className="iconRatings_positiveStar" />
                    <Icon type="star" className="iconRatings_star" />
                    <Icon type="star" className="iconRatings_star" />
                </div>
            </div>
        </div>
    ),

    name: 'Ratings'
};
