import React from 'react';
import ToggleSwitch from '../ToggleSwitch';

const viewModeOptions = (gridId, listId) => [
    {
        id: gridId,
        value: 'grid',
        label: 'Grid',
        selected: true
    },
    {
        id: listId,
        value: 'list',
        label: 'List',
        selected: false
    }
];

export default {
    title: 'Atoms/Toggle Switch',
    component: ToggleSwitch
};

export const Default = {
    render: () => <ToggleSwitch name="toggle1" legend="View Mode: " options={viewModeOptions('grid1', 'list1')} />,

    name: 'Default'
};

export const DefaultWithLegend = {
    render: () => (
        <ToggleSwitch name="toggle2" legend="View Mode: " showLegend options={viewModeOptions('grid2', 'list2')} />
    ),

    name: 'Default with legend'
};

export const Small = {
    render: () => (
        <ToggleSwitch name="toggle3" legend="View Mode: " size="small" options={viewModeOptions('grid3', 'list3')} />
    ),

    name: 'Small'
};
