import React from 'react';
import CategorySidebar from '../CategorySidebar';
import { productFiltersMock } from '../../../../mocks/product/productFilters';
import { productListingMock } from '../../../../mocks/product/productListing';
import { getArgTypes, getArgs } from '../../../tools/controlsHelper';

const listingObject = {
    items: productListingMock.items,
    pagination: productListingMock.pagination,
    i18n: productListingMock.i18n
};

const mockWithParams = { ...productFiltersMock, listing: listingObject };

export default {
    title: 'Components/Category Sidebar',
    component: CategorySidebar,
    argTypes: getArgTypes(CategorySidebar.schema().form)
};

export const Default = {
    render: args => <CategorySidebar data={mockWithParams} settings={args} />,
    args: getArgs(CategorySidebar.schema().form)
};

export const WithSettings = {
    render: args => <CategorySidebar settings={args} />,
    args: getArgs(CategorySidebar.schema().form)
};
