import React from 'react';
import PropTypes from 'prop-types';
import { createNamespace } from '../../../tools/namingTools';

const n = createNamespace('CompareProductDeveloper');

export const CompareProductDeveloper = ({ developer }) => (
    <div {...n('text').withTestId().props}>{developer}</div>
);

CompareProductDeveloper.isRenderable = item => item.developer;

CompareProductDeveloper.propTypes = {
    developer: PropTypes.string
};

CompareProductDeveloper.defaultProps = {
    developer: null
};
