import React from 'react';
import PropTypes from 'prop-types';
import { createNamespace } from '../../../tools/namingTools';

const n = createNamespace('CompareProductSpecifications');

export const CompareProductSpecifications = ({ specification }) => (
    <div>
        {specification && <p {...n('text').withTestId().props}>{specification}</p>}
    </div>
);

CompareProductSpecifications.isRenderable = item => item.specifications && item.specifications.length;

CompareProductSpecifications.propTypes = {
    specification: PropTypes.string
};

CompareProductSpecifications.defaultProps = {
    specification: ''
};
