import React from 'react';
import PropTypes from 'prop-types';
import withListener from '../withListener';

import { OrderableContainer } from './OrderableContainer';
import { orderableComponentsShape } from '../../constants/components/prop-types/orderable-component';
import { getOrderableComponents } from '../../tools/pageOrderableHelper';

export const HomeOrderableContainer = ({ orderableComponents, selectedComponentId, replaceIndex }) => {
    const { HomeOrderableComponents } = getOrderableComponents();

    return (
        <OrderableContainer
            orderableComponents={orderableComponents}
            selectedComponentId={selectedComponentId}
            replaceIndex={replaceIndex}
            componentsList={HomeOrderableComponents}
        />
    );
};

HomeOrderableContainer.schema = () => ({
    name: 'PageOrderableContainer'
});

HomeOrderableContainer.propTypes = {
    orderableComponents: PropTypes.arrayOf(orderableComponentsShape),
    selectedComponentId: PropTypes.string,
    replaceIndex: PropTypes.number
};

HomeOrderableContainer.defaultProps = {
    orderableComponents: [],
    selectedComponentId: '',
    replaceIndex: null
};

export default withListener(HomeOrderableContainer);
