import { useEffect, useState } from 'react';
/*
    filter cart to get the item for which we are viewing the page.
    There's an item in the cart that corresponds to the current application,
    we find it by matching pricing plan id
*/
export const useAppCartItem = (selectedEdition, cart) => {
    const [appCartItem, setAppCartItem] = useState();

    useEffect(() => {
        if (cart?.items) {
            const cartApplication = cart.items.find(cartItem => {
                const { selectedPricingPlanId } = cartItem;
                return selectedEdition === selectedPricingPlanId;
            });
            setAppCartItem(cartApplication);
        }
    }, [cart, selectedEdition]);
    return appCartItem;
};
