import React from 'react';
import PropTypes from 'prop-types';
import { createNamespace } from '../../tools/namingTools';
// IMPORTANT: (s)css should be imported after other atoms/components
import './styles/BundlePage.scss';

const n = createNamespace('BundlePage');

export const BundlePage = ({
    banner,
    header,
    bundle
}) => (
    <div {...n('wrapper').props}>
        {banner && <div {...n('banner').props}>{banner}</div>}
        {header && <div {...n('header').props}>{header}</div>}
        {bundle && <div {...n('bundle').props}>{bundle}</div>}
    </div>
);

BundlePage.propTypes = {
    banner: PropTypes.element,
    header: PropTypes.element,
    bundle: PropTypes.element
};

BundlePage.defaultProps = {
    banner: null,
    header: null,
    bundle: null
};

export default BundlePage;
