import PropTypes from 'prop-types';
import iconType from '../../atoms/icon/types';
import { LEFT, CENTER, RIGHT } from '../alignment';

export const ALIGNMENT_OPTIONS = [LEFT, CENTER, RIGHT];
export const ALIGNMENT_OPTIONS_ENUM = ALIGNMENT_OPTIONS.map(({ value }) => value);

export const linkCardShape = {
    linkInfo: PropTypes.arrayOf(
        PropTypes.shape({
            text: PropTypes.string,
            link: PropTypes.string
        })
    ),
    header: PropTypes.shape({
        name: PropTypes.string,
        navigationLabel: PropTypes.string,
        iconType: PropTypes.oneOf(Object.keys(iconType))
    })
};
