    searchButtonColor: {
        title: 'uieditor.sfbComponent.searchBar.buttonColor.title',
        type: COLORPICKER
    },
    searchIconColor: {
        title: 'uieditor.sfbComponent.searchBar.iconColor.title',
        type: COLORPICKER
    }
