import PropTypes from 'prop-types';
import { ALLOWED_CTA_TYPES } from '../../../actions';

export const callToActionItemsShape = PropTypes.arrayOf(
    PropTypes.shape({
        actionType: PropTypes.oneOf(ALLOWED_CTA_TYPES),
        enabled: PropTypes.bool,
        isPrimary: PropTypes.bool,
        label: PropTypes.string,
        popup: PropTypes.object,
        tooltip: PropTypes.string,
        url: PropTypes.string
    })
);

export const callToActionListShape = PropTypes.shape({
    items: callToActionItemsShape
});

export const callToActionsShape = {
    caption: PropTypes.shape({
        text: PropTypes.string,
        tooltip: PropTypes.string
    }),
    items: callToActionItemsShape
};
