import PropTypes from 'prop-types';

const characteristicsPropTypes = {
    id: PropTypes.number,
    title: PropTypes.string.isRequired,
    position: PropTypes.number.isRequired,
    belowPicture: PropTypes.bool.isRequired,
    highlights: PropTypes.arrayOf(PropTypes.shape({
        highlight: PropTypes.string.isRequired,
        displayCheck: PropTypes.bool.isRequired
    }))
};

export const featuresPropTypes = {
    items: PropTypes.arrayOf(PropTypes.shape({
        id: PropTypes.number.isRequired,
        label: PropTypes.string.isRequired,
        description: PropTypes.string.isRequired,
        imageUrl: PropTypes.string,
        aboveCharacteristics: PropTypes.arrayOf(PropTypes.shape(characteristicsPropTypes)),
        belowCharacteristics: PropTypes.arrayOf(PropTypes.shape(characteristicsPropTypes))
    })),
    i18n: PropTypes.shape({
        title: PropTypes.string,
        next: PropTypes.string,
        prev: PropTypes.string
    })
};
