import PropTypes from 'prop-types';

const tagsPropTypes = {
    name: PropTypes.string,
    group: PropTypes.arrayOf(
        PropTypes.shape({
            id: PropTypes.number,
            name: PropTypes.string,
            type: PropTypes.string,
            url: PropTypes.string
        })
    )
};

const reviewsPropTypes = {
    caption: PropTypes.string,
    numRatings: PropTypes.number,
    rating: PropTypes.number,
    title: PropTypes.string
};

const vendorPropTypes = {
    items: PropTypes.arrayOf(PropTypes.shape({
        href: PropTypes.string,
        label: PropTypes.string
    })),
    title: PropTypes.string
};

export const detailsPropTypes = {
    industries: PropTypes.arrayOf(PropTypes.string),
    reviews: PropTypes.shape(reviewsPropTypes),
    tags: PropTypes.arrayOf(PropTypes.shape(tagsPropTypes)),
    vendor: PropTypes.shape(vendorPropTypes)
};
