import PropTypes from 'prop-types';
import { i18nPropTypes } from './i18n';

const resourceItem = {
    id: PropTypes.number,
    resourceType: PropTypes.string,
    name: PropTypes.string,
    link: PropTypes.string,
    scribdKey: PropTypes.string,
    description: PropTypes.string,
    iFrameFriendlyURL: PropTypes.string,
    resellerAccess: PropTypes.bool
};

export const videosPropTypes = {
    items: PropTypes.arrayOf(
        PropTypes.shape(resourceItem)
    )
};

export const documentsPropTypes = {
    items: PropTypes.arrayOf(
        PropTypes.shape(resourceItem)
    )
};

export const resourcesPropTypes = {
    public: PropTypes.shape({
        documents: PropTypes.shape(documentsPropTypes),
        videos: PropTypes.shape(videosPropTypes)
    }),
    reseller: PropTypes.shape({
        documents: PropTypes.shape(documentsPropTypes),
        videos: PropTypes.shape(videosPropTypes)
    }),
    i18n: PropTypes.shape(i18nPropTypes)
};
