import PropTypes from 'prop-types';

const resourceItem = {
    id: PropTypes.number,
    resourceType: PropTypes.string,
    name: PropTypes.string,
    link: PropTypes.string,
    scribdKey: PropTypes.string,
    description: PropTypes.string,
    iFrameFriendlyURL: PropTypes.string,
    resellerAccess: PropTypes.bool
};

const resourcesItemPropTypes = {
    documents: PropTypes.shape({
        items: PropTypes.arrayOf(
            PropTypes.shape(resourceItem)
        )
    }),
    videos: PropTypes.shape({
        items: PropTypes.arrayOf(
            PropTypes.shape(resourceItem)
        )
    })
};

export const resourcesPropTypes = {
    i18n: PropTypes.shape({
        Documents: PropTypes.string,
        Download: PropTypes.string,
        ResellerResources: PropTypes.string,
        Resources: PropTypes.string,
        Videos: PropTypes.string
    }),
    public: PropTypes.shape(resourcesItemPropTypes),
    reseller: PropTypes.shape(resourcesItemPropTypes)
};
