export const getComponentSchema = component => {
    if (component && component.schema) {
        return component.schema;
    }
    return undefined;
};

export const getComponentSchemaName = component => {
    let componentName = '';
    const schema = getComponentSchema(component);
    if (getComponentSchema(component)) {
        componentName = schema().name;
    }
    return componentName;
};

export const isChildrenOrderableComponent = componentId => {
    if (!componentId) {
        return false;
    }

    const component = window.__ADComponents[componentId.split(':')[0]]; // eslint-disable-line no-underscore-dangle

    return (!!component?.schema().childrenOrderableComponent);
};
