import { replacePlaceholders } from '../stringTools';

export const getBtnCartAriaLabel = (cart, i18nCart = {}, isCartSummaryOpen = false) => {
    const { itemsCount } = cart;

    if (isCartSummaryOpen) {
        return i18nCart.closeCart;
    }

    let ariaLabel = i18nCart.openCart;
    if (itemsCount === 1) {
        ariaLabel += `: ${replacePlaceholders(i18nCart.cartItem, { itemsCount })}`;
    } else if (itemsCount >= 1) {
        ariaLabel += `: ${replacePlaceholders(i18nCart.cartItems, { itemsCount })}`;
    }

    return ariaLabel;
};
