// Determine if slide is in active range of slick-slider
// e.g. If current slide is 2, and slidesToShow is 4, that means only indices [2,3,4,5] is active
export const isSlideInsideActiveRange = (currentSlide = 0, index = 0, slidesToShow = 1) => {
    // Carousel
    if (slidesToShow === 1) {
        return currentSlide === index;
    }
    // Slider
    const higherRange = currentSlide + slidesToShow - 1;
    return index >= currentSlide && index <= higherRange;
};

// Determine if slide is in initial range shown by slick-slider on load
// e.g. If slider enabled and slidesToShow is 8, then only return 0 to 7
// If static (slider disabled), all images are shown in all rows
export const isSlideInsideInitialRange = (index = 0, slidesToShow = 1, maxRows = 1, isSlider = true) => {
    const totalSlides = isSlider ? slidesToShow : (slidesToShow * maxRows);

    return index >= 0 && index < totalSlides;
};
