const monthNames = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December'
];

/**
 Convert a date or unix timestamp to friendly full date (ex: January 1, 1970)
 @param {string|number} date the date to convert to full date format
 @returns {string} The full date
*/
export const getFullDate = date => {
    const dateObj = date ? new Date(date) : null;

    return dateObj ? `${monthNames[dateObj.getMonth()]} ${dateObj.getDate()}, ${dateObj.getFullYear()}` : '';
};
