export const priceFormatter = (locale, currency, price) => {
    const defaultValues = ['', 'NULL'];
    let priceSymbol;
    const finalPrice = [];
    if (!locale || !currency || Number.isNaN(Number(price)) || price === null) { return defaultValues; }

    try {
        const formattedPrice = new Intl.NumberFormat(locale, {
            style: 'currency',
            currency
        }).formatToParts(price);

        formattedPrice.forEach(priceValue => {
            if (priceValue.type === 'currency') {
                priceSymbol = priceValue.value;
            } else if (priceValue.value !== '') { finalPrice.push(priceValue.value); }
        });
    } catch (e) {
        return defaultValues;
    }
    const formattedFinalPrice = finalPrice.join('');

    return [priceSymbol, formattedFinalPrice];
};
