import React from 'react';
import Image from '../../atoms/image/Image';

export const renderBadge = ({ uuid, image, name }) => (
    <Image src={image} key={uuid} alt={name} />
);

export const renderMerchandisingBadges = (merchandisingBadges, namespace) => merchandisingBadges.map(
    ({ uuid, image, name }) => (
        <li {...namespace('merchandisingBadgeItem').props} key={uuid}>
            <Image src={image} alt={name} />
        </li>
    )
);
