import { getPropValue } from './objectTools';

/**
 * get selected logo image alt from global styles
 *
 * @param {String} selectedLogo [darkLogo|logoImage]
 * @returns {String} alt
 */
export const getLogoAlt = selectedLogo => getPropValue(window, `themeSettings.globalStyles.namespaces.storefront.${selectedLogo}.value.alt`);
