export const PROVIDER = 'youtube';
export const REGEX_VIDEO_ID = /(?:youtu\.be\/|youtube\.com(?:\/embed\/|\/v\/|\/watch\?v=|\/user\/\S+|\/ytscreeningroom\?v=))([\w]{10,12})\b/;

export const getYoutubeThumbnail = videoId => {
    if (!videoId) {
        return null;
    }

    return `https://img.youtube.com/vi/${videoId}/hqdefault.jpg`;
};

export const getYoutubeVideoId = url => {
    const matchesArray = url && url.match(REGEX_VIDEO_ID);

    return matchesArray && matchesArray.length ? matchesArray[1] : null;
};
